import React from "react";
import "./DiceRow.css";

function DiceRow(row, dicePerSet = 2) {
  console.log("dice per set: " + dicePerSet);
  return (
    <div key={"row-" + row} className="set">
      <input type="text" className="set_text" />
      {[...Array(dicePerSet)].map((n, i) => (
        <div key={"row-" + row + "-die-" + i} className="die">
          1
        </div>
      ))}
      <div className="equals">=</div>
      <div className="set_total">2</div>
    </div>
  );
}

export default DiceRow;
