import React from "react";
import "./DiceRow.css";

function DiceRow() {
  let dicePerSet = 2;
  var rows = [];
  for (var i = 0; i < dicePerSet; i++) {
    rows.push(<div className="die">{i}</div>);
  }

  return (
    <div className="set">
      <input type="text" className="set_text" />
      {rows}
      <div className="set_total">2</div>
    </div>
  );
}

export default DiceRow;
